import React, {useEffect, useState} from 'react';
import axios from 'axios';

import {useNavigate} from 'react-router';

export const withRouter = (Component) => {
    const Wrapper = (props) => {
        const history = useNavigate();
        return <Component history={history} {...props}/>
    }
    return Wrapper;
}

function EventDetails({match}) {
    const [event, setEvent] = useState(null);

    useEffect(() => {
        const fetchEventDetails = async () => {
            try {
                const response = await axios.get(`http://127.0.0.1:8000/events/${match.params.id}`);
                setEvent(response.data);
            } catch (error) {
                console.error('Error fetching event details:', error);
            }
        };

        fetchEventDetails();
    }, [match.params.id]);

    if (!event) {
        return <div>Loading...</div>;
    }

    const {name, location, time, description, image_url, users} = event;

    return (
        <div>
            <img src={image_url} alt="Event"/>
            <h2>{name}</h2>
            <p>{location}</p>
            <p>{time}</p>
            <p>{description}</p>
            <p>{users.length} человек уже забронировало билеты!</p>
        </div>
    );
}

export default withRouter(EventDetails);
