// Header.js

import React, { useState } from 'react';
import './Header.css';
import LoginModal from '../LoginModal/LoginModal';
import { Link } from 'react-router-dom';

function Header() {
  const [showModal, setShowModal] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('token'));

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
  };

  return (
    <header>
      <Link to="/" className="logo">nFactorial</Link>
      {isLoggedIn ? (
        <div className="login-button" onClick={handleLogout}>Выход</div>
      ) : (
        <div className="login-button" onClick={toggleModal}>Вход</div>
      )}
      {showModal && <LoginModal setIsLoggedIn={setIsLoggedIn} setShowModal={setShowModal} />} {/* Проверьте здесь передачу setIsLoggedIn */}
    </header>
  );
}

export default Header;
