// Main.js

import React, { useState, useEffect } from 'react';
import EventList from '../EventList/EventList';
import axios from 'axios';

function Main() {
  const [events, setEvents] = useState([]);
  const [registeredEventsCount, setRegisteredEventsCount] = useState(0);

  useEffect(() => {
    fetchEvents();
    if (localStorage.getItem('token')) {
      fetchRegisteredEventsCount();
    }
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/events/');
      setEvents(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке мероприятий:', error);
    }
  };

  const fetchRegisteredEventsCount = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/events/my_events/count', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      setRegisteredEventsCount(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке количества зарегистрированных мероприятий:', error);
    }
  };

  const handleViewRegisteredEvents = () => {
    // Здесь можно добавить редирект на страницу "Мои мероприятия"
  };

  return (
    <div className="main">
      <h1>| КОНЦЕРТЫ</h1>
      {registeredEventsCount > 0 && (
        <div>
          <p>Вы зарегистрированы на {registeredEventsCount} мероприятий.</p>
          <button onClick={handleViewRegisteredEvents}>Просмотреть</button>
        </div>
      )}
      <EventList events={events} />
    </div>
  );
}

export default Main;
